# GC Content Calculator - Inline FASTA Version (Fixed)
# Author: Teja Hegde

fasta_sequence = """
>codon_optimized_vaccine_EcoliK12

"""

def gc_content(seq):
    seq = "".join(line.strip() for line in seq.splitlines() if not line.startswith(">") and line.strip())
    g = seq.count("G")
    c = seq.count("C")
    total = len(seq)
    return (round((g + c) / total * 100, 2), total) if total else (0, 0)

gc, length = gc_content(fasta_sequence)
print(f"\nSequence length: {length} bases")
print(f"GC Content: {gc} %\n")
